    private void printTheNumber() {
        String result = Double.toString(currentNumber);
        jLabel1.setText(result);
    }

    private void enterTheNumber(int number) {
        currentNumber = (currentNumber * 10 + number) * factor;
        if (factor != 1) {
            factor = factor / 10;
        }
        printTheNumber();
    }

